import React from 'react';
import {
    StyleSheet,
    Text,
    View,
    TextInput,
    Button,
    TouchableOpacity,
    ScrollView,
    Image
} from 'react-native';

export default class SearchComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            artist: '',
            modalVisible: false,
            search: '',
            results :  [
                {
                   artisteName: "Nujabes",
                   artworkUrl30: "https://www.radiolaser.fr/photo/art/grande/7507965-11577856.jpg?v=1424939002",
                   collectioName: "Modal Soul",
                   trackCensoredName: "Feather",
                   key: 1
                },
            ]
        }
    }
    
    fetchData () {
              fetch("https://itunes.apple.com/search?term=" + this.state.artist)
                  .then(response => response.json())
                  .then(result => {
                    this.setState({
                        results: [...this.state.results, result]
                    })
                  })
        
    }
    onHandleArtistChange = artist => {
        this.setState({
            artist: artist
        });
    }
    onHandleNameChange = name => {
        this.setState({
            name: name
        });
    }
    render() {
        return ( 
        <View>
            <View style={styles.container}>
                <TextInput value={this.state.artist} onChangeText={this.onHandleArtistChange} placeholder=' Search an artist ... ' />
                <TouchableOpacity ><Button title ="Search" onPress = {this.fetchData.bind(this)} /></TouchableOpacity>
            </View>
            <View>
                <ScrollView>
                {this.state.results.map((result,i) => {
                    <View style={styles.item}>
                        <Text>Title: {result.trackCensoredName}</Text>
                    </View>
                })}
                </ScrollView>
            </View>
        </View>
        );
    }
    
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    item: {
        flex: 1,
        flexDirection: 'row',
        padding: 10,
        margin: 5
    }
});
